        i0.ɵɵtext(289, "Verarbeitete Datenarten:");
        i0.ɵɵelementEnd();
        i0.ɵɵtext(290, " Inhaltsdaten (z.B. Eingaben in Onlineformularen), Nutzungsdaten (z.B. besuchte Webseiten, Interesse an Inhalten, Zugriffszeiten), Meta-/Kommunikationsdaten (z.B. Ger\u00E4te-Informationen, IP-Adressen).");
