        i0.ɵɵelementStart(516, "li")(517, "strong");
        i0.ɵɵtext(518, "Auskunftsrecht:");
        i0.ɵɵelementEnd();
        i0.ɵɵtext(519, " Sie haben das Recht, eine Best\u00E4tigung dar\u00FCber zu verlangen, ob betreffende Daten verarbeitet werden und auf Auskunft \u00FCber diese Daten sowie auf weitere Informationen und Kopie der Daten entsprechend den gesetzlichen Vorgaben.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(520, "li")(521, "strong");
        i0.ɵɵtext(522, "Recht auf Berichtigung:");
        i0.ɵɵelementEnd();
        i0.ɵɵtext(523, " Sie haben entsprechend den gesetzlichen Vorgaben das Recht, die Vervollst\u00E4ndigung der Sie betreffenden Daten oder die Berichtigung der Sie betreffenden unrichtigen Daten zu verlangen.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(524, "li")(525, "strong");
        i0.ɵɵtext(526, "Recht auf L\u00F6schung und Einschr\u00E4nkung der Verarbeitung:");
        i0.ɵɵelementEnd();
        i0.ɵɵtext(527, " Sie haben nach Ma\u00DFgabe der gesetzlichen Vorgaben das Recht, zu verlangen, dass Sie betreffende Daten unverz\u00FCglich gel\u00F6scht werden, bzw. alternativ nach Ma\u00DFgabe der gesetzlichen Vorgaben eine Einschr\u00E4nkung der Verarbeitung der Daten zu verlangen.");
        i0.ɵɵelementEnd();
        i0.ɵɵelementStart(528, "li")(529, "strong");
        i0.ɵɵtext(530, "Recht auf Daten\u00FCbertragbarkeit:");
        i0.ɵɵelementEnd();
        i0.ɵɵtext(531, " Sie haben das Recht, Sie betreffende Daten, die Sie uns bereitgestellt haben, nach Ma\u00DFgabe der gesetzlichen Vorgaben in einem strukturierten, g\u00E4ngigen und maschinenlesbaren Format zu erhalten oder deren \u00DCbermittlung an einen anderen Verantwortlichen zu fordern.");
